import React, { useState, useEffect } from 'react';
import { Link, useSearchParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalQuestions: 0,
    hasNextPage: false,
    hasPrevPage: false
  });
  
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  // Get current filter and page from URL params
  const currentPage = parseInt(searchParams.get('page')) || 1;
  const filter = searchParams.get('filter') || 'newest';
  const search = searchParams.get('search') || '';
  const limit = 10;

  // Fetch questions
  useEffect(() => {
    const fetchQuestions = async () => {
      setLoading(true);
      setError('');
      
      try {
        let url = `/api/questions?page=${currentPage}&limit=${limit}`;
        
        // Add filter parameters if needed
        if (filter === 'unanswered') {
          // This would need to be implemented in the backend
          // For now, we'll just use the existing endpoint
          url += '&filter=unanswered';
        }

        const res = await axios.get(url);
        
        if (res.data.success) {
          setQuestions(res.data.questions);
          setPagination(res.data.pagination);
        }
      } catch (error) {
        console.error('Error fetching questions:', error);
        setError('Failed to load questions. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [currentPage, filter]);

  // Handle filter change
  const handleFilterChange = (newFilter) => {
    const newParams = { filter: newFilter, page: '1' };
    if (search) newParams.search = search;
    setSearchParams(newParams);
  };

  // Handle page change
  const handlePageChange = (page) => {
    const newParams = { filter, page: page.toString() };
    if (search) newParams.search = search;
    setSearchParams(newParams);
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    const newParams = { filter, page: '1' };
    if (searchQuery.trim()) newParams.search = searchQuery.trim();
    setSearchParams(newParams);
  };

  // Handle ask question click
  const handleAskQuestion = () => {
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/ask-question');
    } else {
      navigate('/login');
    }
  };

  // Create excerpt from HTML content
  const createExcerpt = (htmlContent, maxLength = 150) => {
    // Strip HTML tags and get plain text
    const textContent = htmlContent.replace(/<[^>]*>/g, '');
    if (textContent.length <= maxLength) return textContent;
    return textContent.substring(0, maxLength) + '...';
  };

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  // Get vote count (for now, we'll show a placeholder since Answer model has votes)
  const getVoteCount = (question) => {
    // This would need to be calculated based on answers' votes
    // For now, return a placeholder
    return 0;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="text-center">
          <h2>Loading questions...</h2>
          <p>Please wait while we fetch the latest questions.</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="alert alert-error">
          {error}
        </div>
        <div className="text-center">
          <button 
            onClick={() => window.location.reload()} 
            className="btn"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Header */}
      <div className="home-header">
        <h1>Questions</h1>
        <div className="header-actions">
          <Link to="/dashboard" className="btn btn-secondary">
            Back to Dashboard
          </Link>
          <Link to="/ask-question" className="btn">
            Ask Question
          </Link>
        </div>
      </div>

      {/* Filter Controls */}
      <div className="filter-controls">
        <button 
          className={`btn ${filter === 'newest' ? 'btn-primary' : 'btn-secondary'}`}
          onClick={() => handleFilterChange('newest')}
        >
          Newest
        </button>
        <button 
          className={`btn ${filter === 'unanswered' ? 'btn-primary' : 'btn-secondary'}`}
          onClick={() => handleFilterChange('unanswered')}
        >
          Unanswered
        </button>
      </div>

      {/* Questions Count */}
      <div className="questions-count">
        <p>{pagination.totalQuestions} questions</p>
      </div>

      {/* Questions List */}
      <div className="questions-list">
        {questions.length === 0 ? (
          <div className="no-questions">
            <h3>No questions found</h3>
            <p>Be the first to ask a question!</p>
            <Link to="/ask-question" className="btn">
              Ask the First Question
            </Link>
          </div>
        ) : (
          questions.map(question => (
            <div key={question._id} className="question-card">
              <div className="question-stats">
                <div className="stat">
                  <span className="stat-number">{getVoteCount(question)}</span>
                  <span className="stat-label">votes</span>
                </div>
                <div className="stat">
                  <span className="stat-number">{question.answers.length}</span>
                  <span className="stat-label">answers</span>
                </div>
                {question.acceptedAnswer && (
                  <div className="stat accepted">
                    <span className="stat-label">âœ“ accepted</span>
                  </div>
                )}
              </div>
              
              <div className="question-content">
                <h3 className="question-title">
                  <Link to={`/questions/${question._id}`}>
                    {question.title}
                  </Link>
                </h3>
                
                <p className="question-excerpt">
                  {createExcerpt(question.description)}
                </p>
                
                <div className="question-meta">
                  <div className="question-tags">
                    {question.tags.map(tag => (
                      <span key={tag._id} className="tag">
                        {tag.name}
                      </span>
                    ))}
                  </div>
                  
                  <div className="question-author">
                    <span>asked {formatDate(question.createdAt)} by </span>
                    <Link to={`/users/${question.author._id}`} className="author-link">
                      {question.author.username}
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Pagination Controls */}
      {pagination.totalPages > 1 && (
        <div className="pagination">
          <div className="pagination-info">
            Page {pagination.currentPage} of {pagination.totalPages}
          </div>
          
          <div className="pagination-controls">
            <button 
              onClick={() => handlePageChange(1)}
              disabled={pagination.currentPage === 1}
              className="btn btn-secondary"
            >
              First
            </button>
            
            <button 
              onClick={() => handlePageChange(pagination.currentPage - 1)}
              disabled={!pagination.hasPrevPage}
              className="btn btn-secondary"
            >
              Previous
            </button>
            
            {/* Page numbers */}
            {Array.from({ length: Math.min(5, pagination.totalPages) }, (_, i) => {
              const startPage = Math.max(1, pagination.currentPage - 2);
              const page = startPage + i;
              
              if (page <= pagination.totalPages) {
                return (
                  <button 
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`btn ${page === pagination.currentPage ? 'btn-primary' : 'btn-secondary'}`}
                  >
                    {page}
                  </button>
                );
              }
              return null;
            })}
            
            <button 
              onClick={() => handlePageChange(pagination.currentPage + 1)}
              disabled={!pagination.hasNextPage}
              className="btn btn-secondary"
            >
              Next
            </button>
            
            <button 
              onClick={() => handlePageChange(pagination.totalPages)}
              disabled={pagination.currentPage === pagination.totalPages}
              className="btn btn-secondary"
            >
              Last
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Questions;
